package iut.sae.algo;

import org.junit.Test;

import junit.framework.TestCase;

public class simpliciteTest extends TestCase{

    /*
   * RLE("")=""
   * RLE("abc")="1a1b1c"
   * RLE("abbccc")="1a2b3c"
   * RLE("aaabaa")="3a1b2a"
   * RLE("aAa")="1a1A1a"
   */

   @Test
   public void testRLE(){
      assertEquals("", simplicite2.RLE("") );
      
      assertEquals("1a1b1c", simplicite2.RLE("abc"));
      assertEquals("1a2b3c", simplicite2.RLE("abbccc"));
      assertEquals("3a1b2a", simplicite2.RLE("aaabaa"));
      assertEquals("1a1A1a", simplicite2.RLE("aAa"));
      assertEquals("9W4W", simplicite2.RLE("WWWWWWWWWWWWW"));
      

   }


   /*
   * RLE(str, 1)=RLE(str)
   * RLE(str, 3)=RLE(RLE(RLE(str)))
   */
   @Test
   public void testRLERecursif(){
      try{
         assertEquals("", simplicite2.RLE("", 1));
         assertEquals("", simplicite2.RLE("", 3));
         
         assertEquals("1a1b1c", simplicite2.RLE("abc", 1));
         assertEquals("1a2b3c", simplicite2.RLE("abbccc", 1));
         assertEquals("3a1b2a", simplicite2.RLE("aaabaa", 1));
         assertEquals("1a1A1a", simplicite2.RLE("aAa", 1));

         assertEquals("111a111b111c", (simplicite2.RLE("abc", 2)));
         assertEquals("311a311b311c", (simplicite2.RLE("abc", 3)));

         String saeIte20=" ";
         assertEquals(saeIte20, simplicite2.RLE("SAE Algo", 20));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }

   /*
  * unRLE(RLE(str))=str
  *
  * unRLE("")=""
  * unRLE("1a1b1c")="abc"
  * unRLE("1a2b3c")="abbccc"
  * unRLE("3a1b2a")="aaabaa"
  * unRLE("1a1A1a")="aAa"
  */


   @Test
   public void testUnRLE(){
      try{
         assertEquals("", simplicite2.unRLE(""));
         assertEquals("abc", simplicite2.unRLE("1a1b1c"));
         assertEquals("abbccc", simplicite2.unRLE("1a2b3c"));
         assertEquals("aaabaa", simplicite2.unRLE("3a1b2a"));
         assertEquals("aAa", simplicite2.unRLE("1a1A1a"));
         assertEquals("WWWWWWWWWWWWW", simplicite2.unRLE("9W4W"));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }

   
   @Test
   public void testUnRLERecursif(){
      try{
         assertEquals("", simplicite2.unRLE("", 1));
         assertEquals("", simplicite2.unRLE("", 3));
         
         assertEquals("abc", simplicite2.unRLE("1a1b1c", 1));
         assertEquals("abbccc", simplicite2.unRLE("1a2b3c", 1));
         assertEquals("aaabaa", simplicite2.unRLE("3a1b2a", 1));
         assertEquals("aAa", simplicite2.unRLE("1a1A1a", 1));

         assertEquals("abc", (simplicite2.unRLE("111a111b111c", 2)));
         assertEquals("abc", (simplicite2.unRLE("311a311b311c", 3)));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }
    
}
